import { useState, useEffect } from "react";
import { useRouter } from "next/router";
import { MOVIE_QUERY } from "../components/Query";
import { useQuery } from "@apollo/client";
import styled from "styled-components";

const Wrapper = styled.div``;

export default function MovieView() {
	const MovieInfo = null;
	const router = useRouter();
	const movieID = router.query._id;

	const { data, error, loading } = useQuery(MOVIE_QUERY, {
		variables: { _id: movieID }
	});

	const fetching = async ({ title }) => {
		/* `http://www.omdbapi.com/?apikey=${
				process.env.MOVIE_API_KEY
			}&t=${title.replace(" ", "+")}` */
		const Info = await fetch("/api/sample");
		console.log(Info);
		return Info;
	};

	if (loading) return <div>loading</div>;
	if (error) {
		console.log(error);
		return <div>error</div>;
	}
	if (!data) return <div>no data found</div>;
	MovieInfo = fetching();

	return <Wrapper>{data.getMovie.movie.name}</Wrapper>;
}
