import { useState, useEffect } from "react";
import { useRouter } from "next/router";
import { MOVIE_QUERY } from "../components/Query";
import { initializeApollo } from "../apollo/apolloClient";
import styled from "styled-components";

const Wrapper = styled.div``;

export default function MovieView() {
	const [MovieInfo, setMovieInfo] = useState(null);
	useEffect(() => {
		querying();
		/* `http://www.omdbapi.com/?apikey=${
				process.env.MOVIE_API_KEY
			}&t=${title.replace(" ", "+")}` */
		fetch("/api/sample")
			.then(res => res.json())
			.then(data => {
				setMovieInfo(data);
			});
	}, []);

	return (
		<Wrapper>
			<div className="Info">
				{MovieInfo !== null ? (
					<div className="Info">
						<p className="year">{MovieInfo.Year}</p>
						<p className="rated">rated {MovieInfo.Rated}</p>
						<p className="runtime">{MovieInfo.Runtime}</p>
					</div>
				) : (
					""
				)}
			</div>
		</Wrapper>
	);
}

export async function ServerSideProps(context) {
	const queryData = await apolloClient.query({
		query: MOVIE_QUERY,
		variables: { _id: context.query._id }
	});
	return {
		props: {
			movie: queryData.getMovie.movie
		}
	};
}
