/* Nested Sorting */

/* hello	bad	
american pie	good
Hello World	better	
Django Unchained	better	
Dark Knight	better	
Inception	oscar	
spirited away	beyond oscar */
const sample = [
	{ name: "hello", myrate: "bad" },
	{ name: "american pie", myrate: "good" },
	{ name: "django unchained", myrate: "better" },
	{ name: "dark knight", myrate: "better" },
	{ name: "inception", myrate: "oscar" },
	{ name: "spirited away", myrate: "beyond oscar" }
];

export function arrangeWithCommons(array) {}

export default function sorter({ sortMethod, movies, sortHierarchy }) {
	const sortedMovies = [];
	const moviesCopy = movies.slice();
