import React, { createContext, useReducer, useState } from "react";
import { DELETE_MOVIE, UPDATE_MOVIE, ADD_MOVIE } from "./Actions";

export const Context = createContext();

const reducer = (state, action) => {
	switch (action.type) {
		case DELETE_MOVIE:
			return {
				...state,
				movies: state.movies.filter(movie => movie._id !== action.payload)
			};
		case ADD_MOVIE:
			return {
				...state,
				movies: [action.payload, ...state.movies]
			};
		case UPDATE_MOVIE:
			return {
				...state,
				movies: state.movies.map(movie =>
					movie._id === action.payload._id ? (movie = action.payload) : movie
				)
			};
		default:
			return state;
	}
};

export const Provider = ({ children }) => {
	const initialState = {
		movies: []
	};
	const [{ movies }, dispatch] = useReducer(reducer, initialState);

	const updateAction = movie => {
		dispatch({
			type: UPDATE_MOVIE,
			payload: movie
		});
	};
	const deleteAction = movie => {
		dispatch({
			type: DELETE_MOVIE,
			payload: movie
		});
	};
	const addAction = movie => {
		dispatch({
			type: ADD_MOVIE,
			payload: movie
		});
	};
	return (
		<Context.Provider
			value={{ movies, actions: { updateAction, deleteAction, addAction } }}
		>
			{children}
		</Context.Provider>
	);
};
