/* Nested Sorting */

export default function sorter({ sortMethod, movies, sortHierarchy }) {
	if (sortMethod.name.sort !== "") {
		movies.sort((a, b) => {
			if (a.name.toLowerCase() > b.name.toLowerCase()) {
				return sortMethod.name.sort === "asc" ? 1 : -1;
			} else {
				return sortMethod.name.sort === "asc" ? -1 : 1;
			}
		});
	}
	if (sortMethod.myrate.sort !== "") {
		movies.sort((a, b) => {
			if (a.myrate > b.myrate) {
				return sortMethod.myrate.sort === "asc" ? 1 : -1;
			} else {
				return sortMethod.myrate.sort === "asc" ? -1 : 1;
			}
		});
	}
}
