/* Nested Sorting */

const sample = [
	{ name: "hello", myrate: 0 },
	{ name: "american pie", myrate: 1 },
	{ name: "django unchained", myrate: 2 },
	{ name: "dark knight", myrate: 2 },
	{ name: "inception", myrate: 3 },
	{ name: "spirited away", myrate: 4 }
];

//Arrange The Array when they have the same value on the given attribute
//like same rate or same name
function arrangeWithCommons({ array, method }) {
	const sortedMovies = [];
	let sortHolder = [];
	for (let i = 0; i < array.length; i++) {
		console.log(i);
		if (i === 0) {
			sortHolder.push(array[i]);
			continue;
		}
		if (array[i][method] === array[i - 1][method]) {
			sortHolder.push(array[i]);
		} else {
			sortedMovies.push(sortHolder);
			sortHolder = [array[i]];
		}
		if (i === array.length - 1) {
			if (sortHolder.length !== 0) {
				sortedMovies.push(sortHolder);
			}
		}
	}
	return sortedMovies;
}
/* arrangeWithCommons({ array: sample, method: "myrate" }); */

const sortMyrate = ({ movies, sortMethod }) => {
	return movies.sort((a, b) => {
		if (a.myrate > b.myrate) {
			return sortMethod.myrate.sort === "asc" ? 1 : -1;
		} else {
			return sortMethod.myrate.sort === "asc" ? -1 : 1;
		}
	});
};

const sortName = ({ movies, sortMethod }) => {
	return movies.sort((a, b) => {
		if (a.name.toLowerCase() > b.name.toLowerCase()) {
			return sortMethod.name.sort === "asc" ? 1 : -1;
		} else {
			return sortMethod.name.sort === "asc" ? -1 : 1;
		}
	});
};

function sorter({ sortMethod, movies, sortPriority }) {
	const sortedMovies = [];
	const moviesCopy = movies.slice();
	if (sortPriority === "myrate") {
		if (sortMethod.myrate.sort === "asc") {
			//sort array by myrate
			const samp = sortMyrate({ movies: moviesCopy, sortMethod });
			//compress array into sub array if they have the same myrate value
			const arranged = arrangeWithCommons({ array: samp, method: "myrate" });
			//sort the individual sub array according to name
			for (let i = 0; i < arranged.length; i++) {
				sortName({ movies: arranged[i], sortMethod });
			}
			console.log(arranged);
		}
	}
}
sorter({
	sortMethod: { name: { sort: "asc" }, myrate: { sort: "asc" } },
	movies: sample,
	sortPriority: "myrate"
});
