import { useState, useEffect } from "react";
import { useRouter } from "next/router";
import { MOVIE_QUERY } from "../components/Query";
import { useQuery } from "@apollo/client";
import styled from "styled-components";

const Wrapper = styled.div``;

export default function MovieView() {
	let MovieInfo = null;
	const router = useRouter();
	const movieID = router.query._id;

	const { data, error, loading } = useQuery(MOVIE_QUERY, {
		variables: { _id: movieID }
	});

	const fetching = async ({ title }) => {
		/* `http://www.omdbapi.com/?apikey=${
				process.env.MOVIE_API_KEY
			}&t=${title.replace(" ", "+")}` */
		const Info = await fetch("/api/sample").then(res =>
			JSON.stringify(res.body)
		);
		console.log(Info);
		return Info;
	};

	if (loading) return <div>loading</div>;
	if (error) {
		console.log(error);
		return <div>error</div>;
	}
	if (!data) return <div>no data found</div>;
	const { movie } = data?.getMovie;
	MovieInfo = fetching({ title: movie.name });

	return (
		<Wrapper>
			<div className="Title">{movie.name}</div>
			<div className="Info">
				{MovieInfo !== (null && "undefined") ? (
					<div className="Info">
						<p className="year">{MovieInfo.Year}</p>
						<p className="rated">rated {MovieInfo.Rated}</p>
						<p className="runtime">{MovieInfo.Runtime}</p>
					</div>
				) : (
					""
				)}
			</div>
		</Wrapper>
	);
}
