import styled from "styled-components";
import { useState } from "react";
import { ADD_MOVIE_MUTATION } from "../components/Mutations";
import { MOVIES_QUERY } from "../components/Query";
import { useQuery, useMutation } from "@apollo/client";
import Movietable from "../components/Movietable.jsx";

const Wrapper = styled.div`
	padding: 40px;
	table {
		min-width: 1000px;
	}
	.HeadContainer {
		display: grid;
		grid-template-columns: 1fr 1fr 3fr;
		padding: 50px 0px;
		.FormContainer {
			form {
				display: grid;
				grid-template-columns: 1fr 1fr;
				grid-gap: 20px;
				input[type="submit"] {
					grid-column: 1/3;
				}
			}
		}
	}
`;

export default function Home() {
	let initialSort = "name";
	let initialSearchFilter = "";
	if (typeof window !== "undefined") {
		initialSort = localStorage.getItem("sort")
			? localStorage.getItem("sort")
			: initialSort;
		initialSearchFilter = localStorage.getItem("search")
			? localStorage.getItem("search")
			: initialSearchFilter;
	}
	const [sortMethod, setSortMethod] = useState(initialSort);
	const [searchFilter, setSearchFilter] = useState(initialSearchFilter);
	//Querying the Movies
	const { data, error, loading } = useQuery(MOVIES_QUERY);

	//Mutation for Adding Movie
	const [addMovie, { data: addMovieData }] = useMutation(ADD_MOVIE_MUTATION, {
		update: (proxy, { data: { addMovie } }) => {
			const newMovie = addMovie.movie;
			const data = proxy.readQuery({
				query: MOVIES_QUERY
			});
			proxy.writeQuery({
				query: MOVIES_QUERY,
				data: {
					...data,
					getMovies: {
						movies: [...data.getMovies.movies, newMovie]
					}
				}
			});
		}
	});

	const addMovieSubmit = e => {
		e.preventDefault();
		const form = e.target;
		const formData = new window.FormData(form);
		const name = formData.get("name");
		const myrate = formData.get("myrate");
		form.reset();
		addMovie({ variables: { name, myrate: Number(myrate) } });
	};

	const sortMethodChange = e => {
		localStorage.setItem("sort", e.target.value);
		setSortMethod(e.target.value);
	};

	if (loading) return <div>loading</div>;
	if (error) {
		console.log(error);
		return <div>error</div>;
	}
	if (!data) return <div>no data found</div>;
	const movies = data.getMovies.movies.slice();
	movies.sort((a, b) => {
		if (
			typeof a[sortMethod] === number
				? a[sortMethod] > b[sortMethod]
				: a[sortMethod].toLowerCase() > b[sortMethod].toLowerCase()
		) {
			return 1;
		} else {
			return -1;
		}
	});
	let searchFilteredMovies;
	if (searchFilter !== "") {
		searchFilteredMovies = movies.filter(movie => {
			console.log(
				movie.name.toLowerCase().includes(searchFilter.toLowerCase())
			);
			return movie.name.toLowerCase().includes(searchFilter.toLowerCase());
		});
	} else {
		searchFilteredMovies = movies;
	}
	return (
		<Wrapper>
			<h1 className="title">My Movies</h1>
			<div className="HeadContainer">
				<div className="Tools">
					<h3>search</h3>
					<div className="search">
						<input
							type="text"
							name="search"
							id=""
							value={searchFilter}
							onChange={e => setSearchFilter(e.target.value)}
						/>
					</div>
					<div className="sortMethod">
						<h3>sort by:</h3>
						<select
							name="myrate"
							defaultValue={sortMethod}
							onChange={sortMethodChange}
						>
							<option value="name">name</option>
							<option value="myrate">myrate</option>
						</select>
					</div>
				</div>
				<div className="FormContainer">
					<h2 className="formTitle">Add Movie</h2>
					<form onSubmit={addMovieSubmit}>
						<input type="text" name="name" />
						<select name="myrate">
							<option value={1}>bad</option>
							<option value={2}>good</option>
							<option value={3}>better</option>
							<option value={4}>oscar winning</option>
							<option value={5}>beyond oscar</option>
						</select>
						<input type="submit" value="add" />
					</form>
				</div>
			</div>
			<table className="Movies">
				<thead>
					<tr>
						<th>name</th>
						<th>myrate</th>
						<th></th>
						<th></th>
						<th></th>
					</tr>
				</thead>
				<tbody>
					{searchFilteredMovies.map(movie => (
						<Movietable
							key={movie._id}
							details={{
								_id: movie._id,
								name: movie.name,
								myrate: movie.myrate
							}}
						/>
					))}
				</tbody>
			</table>
		</Wrapper>
	);
}
