import { useState, useEffect } from "react";
import { useRouter } from "next/router";
import { MOVIE_QUERY } from "../components/Query";
import { useQuery } from "@apollo/client";
import styled from "styled-components";

const Wrapper = styled.div``;

export default function MovieView() {
	const [state, setstate] = useState(null);
	let MovieInfo = [];
	const router = useRouter();
	const movieID = router.query._id;

	const { data, error, loading } = useQuery(MOVIE_QUERY, {
		variables: { _id: movieID }
	});

	if (loading) return <div>loading</div>;
	if (error) {
		console.log(error);
		return <div>error</div>;
	}
	if (!data) return <div>no data found</div>;
	const { movie } = data?.getMovie;

	/* `http://www.omdbapi.com/?apikey=${
				process.env.MOVIE_API_KEY
			}&t=${title.replace(" ", "+")}` */
	const y = fetch("/api/sample")
		.then(res => res.json())
		.then(data => {
			MovieInfo.push(data);
			return data;
		});

	return (
		<Wrapper>
			<div className="Title">{movie.name}</div>
			<div className="Info">
				{MovieInfo.length !== 0 ? (
					<div className="Info">
						<p className="year">{MovieInfo[0].Year}</p>
						<p className="rated">rated {MovieInfo[0].Rated}</p>
						<p className="runtime">{MovieInfo[0].Runtime}</p>
					</div>
				) : (
					""
				)}
			</div>
		</Wrapper>
	);
}
