/* Nested Sorting */

const sample = [
	[
		{
			__typename: "Movie",
			name: "Inception",
			_id: "5f9a086a89cc6d039a0883eb",
			myrate: 3
		},
		{
			__typename: "Movie",
			name: "spirited away",
			_id: "5f9a087489cc6d039a0883ec",
			myrate: 5
		},
		{
			__typename: "Movie",
			name: "Django Unchained",
			_id: "5f9a088689cc6d039a0883ed",
			myrate: 3
		},
		{
			__typename: "Movie",
			name: "Dark Knight",
			_id: "5f9a088e89cc6d039a0883ee",
			myrate: 3
		},
		{
			__typename: "Movie",
			name: "Hello World",
			_id: "5f9a089889cc6d039a0883ef",
			myrate: 3
		},
		{
			__typename: "Movie",
			name: "american pie",
			_id: "5f9a08d589cc6d039a0883f0",
			myrate: 2
		},
		{
			__typename: "Movie",
			name: "hello",
			_id: "5f9a091089cc6d039a0883f1",
			myrate: 1
		},
		{
			__typename: "Movie",
			name: "Titanic",
			_id: "5f9a86a389cc6d039a0883f2",
			myrate: 3
		}
	]
];

//Arrange The Array when they have the same value on the given attribute
//like same rate or same name
function arrangeWithCommons({ array, method }) {
	const sortedMovies = [];
	let sortHolder = [];
	for (let i = 0; i < array.length; i++) {
		if (i === 0) {
			sortHolder.push(array[i]);
			continue;
		}
		if (array[i][method] === array[i - 1][method]) {
			sortHolder.push(array[i]);
		} else {
			sortedMovies.push(sortHolder);
			sortHolder = [array[i]];
		}
		if (i === array.length - 1) {
			if (sortHolder.length !== 0) {
				sortedMovies.push(sortHolder);
			}
		}
	}
	return sortedMovies;
}

const sortMyrate = ({ movies, sortMethod }) => {
	return movies.sort((a, b) => {
		if (a.myrate > b.myrate) {
			return sortMethod.myrate.sort === "asc" ? 1 : -1;
		} else {
			return sortMethod.myrate.sort === "asc" ? -1 : 1;
		}
	});
};

const sortName = ({ movies, sortMethod }) => {
	return movies.sort((a, b) => {
		if (a.name.toLowerCase() > b.name.toLowerCase()) {
			return sortMethod.name.sort === "asc" ? 1 : -1;
		} else {
			return sortMethod.name.sort === "asc" ? -1 : 1;
		}
	});
};

function sorter({ sortMethod, movies, sortPriority }) {
	const sortedMovies = [];
	const moviesCopy = movies.slice();
	if (sortPriority === "myrate") {
		//sort array by myrate
		const sortedRate = sortMyrate({ movies: moviesCopy, sortMethod });
		//compress array into sub array if they have the same myrate value
		const arranged = arrangeWithCommons({
			array: sortedRate,
			method: "myrate"
		});
		//sort the individual sub array according to name
		for (let i = 0; i < arranged.length; i++) {
			sortName({ movies: arranged[i], sortMethod });
		}
		//push all of the sorted array and subarray to sortedMovies
		arranged.map(subArray => {
			subArray.map(arr => {
				sortedMovies.push(arr);
			});
		});

		return sortedMovies;
	} else if (sortPriority === "name") {
		//sort array by name
		const sortedName = sortName({ movies: moviesCopy, sortMethod });
		//compress array into sub array if they have the same name value
		const arranged = arrangeWithCommons({
			array: sortedName,
			method: "name"
		});
		//sort the individual sub array according to name
		for (let i = 0; i < arranged.length; i++) {
			sortMyrate({ movies: arranged[i], sortMethod });
		}
		//push all of the sorted array and subarray to sortedMovies
		arranged.map(subArray => {
			subArray.map(arr => {
				sortedMovies.push(arr);
			});
		});
		return sortedMovies;
	} else {
		if (sortMethod.name.sort !== "") {
			return sortName({ movies, sortMethod });
		} else {
			return sortMyrate({ movies, sortMethod });
		}
	}
}

console.log(
	sorter({
		sortMethod: { name: { sort: "asc" }, myrate: { sort: "asc" } },
		movies: sample,
		sortPriority: "myrate"
	})
);
