import { useState, useEffect } from "react";
import { MOVIE_QUERY } from "../components/Query";
import styled from "styled-components";

export default function MovieView({ _id, name, myrate }) {
	const [MovieInfo, setMovieInfo] = useState(null);
	useEffect(() => {
		/* `http://www.omdbapi.com/?apikey=${
				process.env.MOVIE_API_KEY
			}&t=${name.replace(" ", "+")}` */
		fetch("/api/sample")
			.then(res => res.json())
			.then(data => {
				setMovieInfo(data);
			});
	}, []);

	return (
		<div>
			<div className="Title">{name}</div>
			<div className="Info">
				{MovieInfo !== null ? (
					<div className="Info">
						<p className="year">{MovieInfo.Year}</p>
						<p className="rated">rated {MovieInfo.Rated}</p>
						<p className="runtime">{MovieInfo.Runtime}</p>
					</div>
				) : (
					""
				)}
			</div>
		</div>
	);
}

export async function ServerSideProps(context) {
	const queryData = await apolloClient.query({
		query: MOVIE_QUERY,
		variables: { _id: context.query._id }
	});
	return {
		props: {
			movie: queryData.getMovie.movie
		}
	};
}
