/* Nested Sorting */

const sample = [
	{ name: "hello", myrate: 0 },
	{ name: "american pie", myrate: 1 },
	{ name: "django unchained", myrate: 2 },
	{ name: "dark knight", myrate: 2 },
	{ name: "inception", myrate: 3 },
	{ name: "spirited away", myrate: 4 }
];

export function arrangeWithCommons({ array, method }) {
	const sortedMovies = [];
	let sortHolder = [];
	for (let i = 0; i > array.length; i++) {
		if (i === 0) {
			sortHolder.push(array[method]);
		}
	}
	console.log(sortHolder);
}
console.log(arrangeWithCommons({ array: sample, method: "myrate" }));

